import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import bcrypt from 'bcryptjs'; // Import bcrypt

function Signup() {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Hash the password using bcrypt
    const hashedPassword = await bcrypt.hash(formData.password, 10); // You can adjust the saltRounds (10 in this case)

    // Send the hashed password to the server
    axios
      .post('http://localhost:5555/users', {
        username: formData.username,
        password: hashedPassword,
      })
      .then((response) => {
        console.log('Success:', response.data);
        setErrorMessage('');
        navigate('/login');
      })
      .catch((error) => {
        console.error('Error:', error);
        if (error.response && error.response.status === 406) {
          setErrorMessage('Sign-up failed. Please try again or contact support.');
        } else {
          setErrorMessage('An unexpected error occurred. Please try again later.');
        }
      });
  };

  return (
    <div>
      <h2>Sign Up</h2>
      {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Username:
            <input type="text" name="username" value={formData.username} onChange={handleChange} required />
          </label>
        </div>
        <div>
          <label>
            Password:
            <input type="password" name="password" value={formData.password} onChange={handleChange} required />
          </label>
        </div>
        <div>
          <button type="submit">Sign Up</button>
        </div>
      </form>
    </div>
  );
}

export default Signup;
