import React, { useEffect, useState } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

const containerStyle = {
  width: '800px',
  height: '400px',
  borderRadius: '5px'
};

const center = {
  lat: 32,
  lng: 20
};

const indexFunds = {
  'United States': {
    name: 'S&P 500',
    ticker: '^GSPC'
  },
  'Europe': {
    name: 'Euro Stoxx 50',
    ticker: '^STOXX50E'
  },
  // Add more index funds as needed
};

const locations = [
  {
    name: 'United States',
    lat: 30.0902,
    lng: -95.7129
  },
  {
    name: 'Europe',
    lat: 54.5260,
    lng: 15.2551
  },
  // Add more locations as needed
];

export default function MarketWatch() {
  const [apiKey, setApiKey] = useState('');
  const [error, setError] = useState(null);
  const [locationData, setLocationData] = useState(locations);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch both API key and historical data in parallel
        const [apiKeyResponse, historicalDataResponse] = await Promise.all([
          fetch('/api/maps/config'),
          fetch('/api/yfinance-historical-data')
        ]);

        if (!apiKeyResponse.ok || !historicalDataResponse.ok) {
          throw new Error(`Network response was not ok.`);
        }

        const [apiKeyData, historicalData] = await Promise.all([
          apiKeyResponse.json(),
          historicalDataResponse.json()
        ]);

        setApiKey(apiKeyData.apiKey);
        updateMarkers(historicalData);
      } catch (error) {
        console.error('Error fetching data:', error);
        setError('Error loading data!');
      }
    };

    fetchData();
  }, []);

  const updateMarkers = (historicalData) => {
    if (historicalData) {
      const updatedLocations = locationData.map((location) => {
        const indexFund = indexFunds[location.name];
        if (indexFund && historicalData[indexFund.ticker]) {
          const historicalPrices = historicalData[indexFund.ticker];
          const currentPrice = historicalPrices[historicalPrices.length - 1].close;
          const previousPrice = historicalPrices[historicalPrices.length - 2].close;
          const isPriceIncreased = currentPrice > previousPrice;

          return {
            ...location,
            isPriceIncreased,
          };
        }
        return location;
      });

      setLocationData(updatedLocations);
    }
  };

  return (
    <>
      {error ? (
        <p>{error}</p>
      ) : apiKey ? (
        <LoadScript googleMapsApiKey={apiKey}>
          <GoogleMap mapContainerStyle={containerStyle} center={center} zoom={2}>
            {locationData.map((location, index) => (
              <Marker
                key={index}
                position={{ lat: location.lat, lng: location.lng }}
                title={location.name}
                icon={{
                  url: location.isPriceIncreased
                    ? 'https://www.freeiconspng.com/uploads/green-spot-light-png-11.png'
                    : 'https://www.freeiconspng.com/uploads/red-spot-light-png-6.png',
                  scaledSize: { width: 100, height: 100 },
                }}
              />
            ))}
          </GoogleMap>
        </LoadScript>
      ) : (
        <p>Loading map...</p>
      )}
    </>
  );
}
