import React from 'react';
import StocksList from '../StocksList';
import MarketWatch from '../MarketWatch';
import NavBar from '../NavBar';
import { Navigate } from 'react-router-dom';

function UserDashboard({ currentUser, logout, selectedComponent }) {
  // Check if currentUser exists and has a username property
  if (!currentUser || !currentUser.username) {
    // Redirect to home if no user is logged in
    return <Navigate to="/" replace />;
  }

  return (
    <div className='user-details'>
      <h2>{currentUser.username}: User Dashboard</h2>
      {/* Conditionally render the NavBar based on the selectedComponent */}
      {selectedComponent === 'stocks' && <NavBar currentUser={currentUser} logout={logout} />}
      {selectedComponent === 'marketWatch' && <NavBar currentUser={currentUser} logout={logout} />}
      {selectedComponent === 'stocks' && <StocksList currentUser={currentUser} logout={logout} />}
      {selectedComponent === 'marketWatch' && <MarketWatch currentUser={currentUser} logout={logout} />}
    </div>
  );
}

export default UserDashboard;
