import React, { useEffect, useState } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import axios from 'axios'; // Import axios

const containerStyle = {
  width: '800px',
  height: '400px',
  borderRadius: '5px',
};

const center = {
  lat: 32,
  lng: 20,
};

const indexFunds = {
  'United States': {
    name: 'S&P 500',
    ticker: '^GSPC', // Replace with the Yahoo Finance symbol for S&P 500
  },
  'Europe': {
    name: 'Euro Stoxx 50',
    ticker: '^STOXX50E', // Replace with the Yahoo Finance symbol for Euro Stoxx 50
  },
  'Asia': {
    name: 'Nikkei 225',
    ticker: '^N225', // Replace with the Yahoo Finance symbol for Nikkei 225
  },
  'Australia': {
    name: 'S&P/ASX 200',
    ticker: '^AXJO', // Replace with the Yahoo Finance symbol for S&P/ASX 200
  },
  'Canada': {
    name: 'S&P/TSX Composite',
    ticker: '^GSPTSE', // Replace with the Yahoo Finance symbol for S&P/TSX Composite
  },
};

const locations = [
  {
    name: 'United States',
    lat: 30.0902,
    lng: -95.7129,
  },
  {
    name: 'Europe',
    lat: 54.5260,
    lng: 15.2551,
  },
  {
    name: 'Asia',
    lat: 34.0479,
    lng: 100.6197,
  },
  {
    name: 'Australia',
    lat: -30.2744,
    lng: 133.7751,
  },
  {
    name: 'Canada',
    lat: 50.1304,
    lng: -106.3468,
  },
];

export default function MarketWatch() {
  const [apiKey, setApiKey] = useState('');
  const [error, setError] = useState(null);
  const [locationData, setLocationData] = useState(locations);

  useEffect(() => {
    fetch('/api/maps/config')
      .then((response) => response.json())
      .then((data) => setApiKey(data.apiKey))
      .catch((error) => {
        console.error('Error fetching Google Maps API key:', error);
        setError('Error loading Google Maps!');
      });

    // Fetch historical data for multiple tickers using Yahoo Finance via your backend
    const fetchHistoricalData = async () => {
      try {
        const response = await axios.get('/api/yahoo-finance-data'); // Replace with your actual backend endpoint
        if (response.status !== 200) {
          throw new Error(`Network response was not ok: ${response.status}`);
        }
        const historicalData = response.data;

        // Update marker data based on historical data
        updateMarkers(historicalData);
      } catch (error) {
        console.error('Error fetching historical data:', error);
        setError('Error loading historical data!');
      }
    };

    fetchHistoricalData();
  }, []);

  const updateMarkers = (historicalData) => {
    if (historicalData) {
      const updatedLocations = locationData.map((location) => {
        const indexFund = indexFunds[location.name];
        if (indexFund && historicalData[indexFund.ticker]) {
          const historicalPrices = historicalData[indexFund.ticker];
          const currentPrice = historicalPrices[historicalPrices.length - 1].Close;
          const previousPrice = historicalPrices[historicalPrices.length - 2].Close;
          const isPriceIncreased = currentPrice > previousPrice;

          return {
            ...location,
            isPriceIncreased,
          };
        }
        return location;
      });

      setLocationData(updatedLocations);
    }
  };

  return (
    <>
      {error ? (
        <p>{error}</p>
      ) : apiKey ? (
        <LoadScript googleMapsApiKey={apiKey}>
          <GoogleMap mapContainerStyle={containerStyle} center={center} zoom={2}>
            {locationData.map((location, index) => (
              <Marker
                key={index}
                position={location}
                title={location.name}
                icon={{
                  url: location.isPriceIncreased
                    ? 'https://www.freeiconspng.com/uploads/green-spot-light-png-11.png'
                    : 'https://www.freeiconspng.com/uploads/red-spot-light-png-6.png',
                  scaledSize: { width: 100, height: 100 },
                }}
              />
            ))}
          </GoogleMap>
        </LoadScript>
      ) : (
        <p>Loading map...</p>
      )}
    </>
  );
}
