import React, { useEffect, useState } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

const containerStyle = {
  width: '800px',
  height: '400px',
  borderRadius: '5px'
};

const center = {
  lat: 32,
  lng: 20
};

// Define your index funds with their ticker symbols
const indexFunds = {
  'United States': {
    name: 'S&P 500',
    ticker: 'SPY'
  },
  'Europe': {
    name: 'Euro Stoxx 50',
    ticker: 'STOXX50'
  },
  'Asia': {
    name: 'Nikkei 225',
    ticker: 'NIKKEI225'
  },
  'Australia': {
    name: 'S&P/ASX 200',
    ticker: 'ASX200'
  },
  'Canada': {
    name: 'S&P/TSX Composite',
    ticker: 'TSX'
  }
};

const locations = [
  {
    name: 'United States',
    lat: 30.0902,
    lng: -95.7129
  },
  {
    name: 'Europe',
    lat: 54.5260,
    lng: 15.2551
  },
  {
    name: 'Asia',
    lat: 34.0479,
    lng: 100.6197
  },
  {
    name: 'Australia',
    lat: -30.2744,
    lng: 133.7751
  },
  {
    name: 'Canada',
    lat: 50.1304,
    lng: -106.3468
  }
];

export default function MarketWatch() {
  const [apiKey, setApiKey] = useState('');
  const [error, setError] = useState(null);
  const [indexFundData, setIndexFundData] = useState({}); // To store index fund data
  const [locationData, setLocationData] = useState(locations);

  useEffect(() => {
    // Fetch Google Maps API key
    fetch('/api/maps/config')
      .then((response) => response.json())
      .then((data) => setApiKey(data.apiKey))
      .catch((error) => {
        console.error('Error fetching Google Maps API key:', error);
        setError('Error loading Google Maps!');
      });

    // Fetch current and old index fund prices
    fetch('/api/index-fund-prices')
      .then((response) => response.json())
      .then((data) => {
        // Store the fetched index fund data in state
        setIndexFundData(data);

        // Update marker data based on index fund data
        updateMarkers(data);
      })
      .catch((error) => {
        console.error('Error fetching index fund prices:', error);
        setError('Error loading index fund data!');
      });
  }, []);

  // Function to update markers based on index fund data
  const updateMarkers = (indexFundData) => {
    if (indexFundData) {
      const updatedLocations = locations.map((location) => {
        const indexFund = indexFunds[location.name];
        if (indexFund) {
          const currentPrice = indexFundData[indexFund.ticker];
          const oldPrice = indexFundData[`${indexFund.ticker}_old`];
          // Compare current and old prices
          const isPriceIncreased = currentPrice > oldPrice;

          return {
            ...location,
            isPriceIncreased,
          };
        }
        return location;
      });

      setLocationData(updatedLocations);
    }
  };

  return (
    <>
      {error ? (
        <p>{error}</p>
      ) : apiKey ? (
        <LoadScript googleMapsApiKey={apiKey}>
          <GoogleMap mapContainerStyle={containerStyle} center={center} zoom={2}>
            {locationData.map((location, index) => (
              <Marker
                key={index}
                position={location}
                title={location.name}
                icon={{
                  url: location.isPriceIncreased
                    ? 'https://www.freeiconspng.com/uploads/green-spot-light-png-11.png'
                    : 'https://www.freeiconspng.com/uploads/red-spot-light-png-6.png',
                  scaledSize: { width: 100, height: 100 },
                }}
              />
            ))}
          </GoogleMap>
        </LoadScript>
      ) : (
        <p>Loading map...</p>
      )}
    </>
  );
}
