import React from 'react';
import { Routes, Route } from 'react-router-dom';
import StocksList from '../StocksList';
import WatchList from '../WatchList';
import MarketWatch from '../MarketWatch';
import ChatBot from '../ChatBot';
import NavBar from '../NavBar';

function UserDashboard({ currentUser, logout }) {
  // Check if currentUser exists and has a username property
  if (!currentUser || !currentUser.username) {
    return (
      <div className='user-details'>
        <h2>User Dashboard</h2>
        <p>No user logged in.</p>
      </div>
    );
  }

  return (
    <div className='user-details'>
      <h2>{currentUser.username}: User Dashboard</h2>
      <NavBar currentUser={currentUser} logout={logout} />
      <Routes>
        <Route path="/dashboard" element={<StocksList />} />
        <Route path="/dashboard/watch-list" element={<WatchList />} />
        <Route path="/dashboard/market-watch" element={<MarketWatch />} />
        <Route path="/dashboard/chat-bot" element={<ChatBot />} />
      </Routes>
    </div>
  );
}

export default UserDashboard;
