import React, { useEffect, useState } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

const containerStyle = {
  width: '800px',
  height: '400px',
  borderRadius: '5px'
};

const center = {
  lat: 32,
  lng: 20
};

export default function MarketWatch() {
  const [apiKey, setApiKey] = useState("");
  const [error, setError] = useState(null);
  const [marketStatus, setMarketStatus] = useState(false); // Track market status for the United States

  useEffect(() => {
    fetch('/api/maps/config')
      .then(response => response.json())
      .then(data => setApiKey(data.apiKey))
      .catch(error => {
        console.error("Error fetching Google Maps API key:", error);
        setError('Error loading Google Maps!');
      });
  }, []);

  useEffect(() => {
    // Fetch stock data for S&P 500 (as a reference for the United States market)
    fetch(`/api/search-stock?query=^GSPC`)
      .then(response => response.json())
      .then(stockData => {
        // Determine market status based on your criteria (e.g., comparing stock price)
        const isMarketUp = stockData && stockData.price > 0; // Example condition
        setMarketStatus(isMarketUp);
      })
      .catch(error => {
        console.error("Error fetching stock data for the United States:", error);
      });
  }, []);

  return (
    <>
      {error ? (
        <p>{error}</p>
      ) : apiKey ? (
        <LoadScript googleMapsApiKey={apiKey}>
          <GoogleMap mapContainerStyle={containerStyle} center={center} zoom={2}>
            <Marker
              position={center}
              title="United States"
              icon={{
                // Set the marker icon based on market status
                url: marketStatus
                  ? '../green.png' // Green marker icon path
                  : '../red.jpeg', // Red marker icon path
                scaledSize: { width: 32, height: 32 } // Adjust width and height as needed
              }}
            />
          </GoogleMap>
        </LoadScript>
      ) : (
        <p>Loading map...</p>
      )}
    </>
  );
}
