import React, { useState, useEffect } from 'react';
import UserPanel from './components/UserPanel/user_index';
import NavBar from './components/NavBar'; // Import the NavBar component
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom'; // Import React Router components




const POST_HEADERS = {
  'Content-Type': 'application/json',
  'Accepts': 'application/json'
}



function App() {
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    async function checkSession() {
      const response = await fetch('/check_session')

      if (response.ok) {
        const data = await response.json()
        setCurrentUser( data )
      }
    }
    checkSession()
  }, [])

  async function attemptSignup(userInfo) {
    const res = await fetch('/users', {
      method: 'POST',
      headers: POST_HEADERS,
      body: JSON.stringify(userInfo)
    })
    if (res.ok) {
      const data = await res.json()
      setCurrentUser(data)
    } else {
      const errorData = await res.json();
      alert(errorData.error || 'Invalid sign up'); // Display the error from backend
    }
  }


async function attemptLogin(userInfo) {
    const res = await fetch('login', {
      method: 'POST',
      headers: POST_HEADERS,
      body: JSON.stringify(userInfo)
    })
    if (res.ok) {
      const data = await res.json()
      setCurrentUser(data)
    } else {
      alert('Invalid login') // Fix this message
    }
}


  function logout() {
    setCurrentUser(null)
    fetch('/logout', {
      method: 'DELETE'
    })
  }


  return (
    <Router>
      {/* Include the NavBar */}
      <NavBar currentUser={currentUser} logout={logout} />
      {/* Define routes using Switch */}
      <Switch>
        <Route path="/dashboard">
          <UserPanel
            currentUser={currentUser}
            attemptLogin={attemptLogin}
            attemptSignup={attemptSignup}
            logout={logout}
          />
        </Route>
        {/* Add more routes as needed */}
        <Route path="/other-route">
          <OtherComponent />
        </Route>
        <Route path="/">
          <HomeComponent />
        </Route>
      </Switch>
    </Router>
  );
}


export default App;
