import React, { useState, useEffect } from 'react';
import UserPanel from './components/UserPanel/user_index';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import UserDashboard from './components/UserPanel/UserDashboard'; // Import UserDashboard
import StocksList from './components/StocksList'; // Import StocksList
import MarketWatch from './components/MarketWatch'; // Import MarketWatch
import Home from './components/Home';

const POST_HEADERS = {
  'Content-Type': 'application/json',
  'Accepts': 'application/json'
}

function App() {
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    async function checkSession() {
      const response = await fetch('/check_session')

      if (response.ok) {
        const data = await response.json()
        setCurrentUser(data)
      }
    }
    checkSession()
  }, [])

  async function attemptSignup(userInfo) {
    const res = await fetch('/users', {
      method: 'POST',
      headers: POST_HEADERS,
      body: JSON.stringify(userInfo)
    })
    if (res.ok) {
      const data = await res.json()
      setCurrentUser(data)
    } else {
      const errorData = await res.json();
      alert(errorData.error || 'Invalid sign up');
    }
  }

  async function attemptLogin(userInfo) {
    const res = await fetch('login', {
      method: 'POST',
      headers: POST_HEADERS,
      body: JSON.stringify(userInfo)
    })
    if (res.ok) {
      const data = await res.json()
      setCurrentUser(data)
      // Redirect to dashboard after successful login
      return <Navigate to="/dashboard" replace />;
    } else {
      alert('Invalid login');
    }
  }

  function logout() {
    setCurrentUser(null)
    fetch('/logout', {
      method: 'DELETE'
    });
    // Redirect to home after logout
    return <Navigate to="/" replace />;
  }

  return (
    <Router>
      <Routes>
        <Route path="/dashboard" element={<UserDashboard currentUser={currentUser} logout={logout} />} />
        <Route path="/stocks" element={<StocksList />} />
        <Route path="/market-watch" element={<MarketWatch />} />
        <Route path="/signup" element={<UserPanel currentUser={currentUser} attemptLogin={attemptLogin} attemptSignup={attemptSignup} logout={logout} />} />
        <Route path="/login" element={<UserPanel currentUser={currentUser} attemptLogin={attemptLogin} attemptSignup={attemptSignup} logout={logout} />} />
        <Route
          path="/"
          element={<Home attemptLogin={attemptLogin} attemptSignup={attemptSignup} />}
        />
      </Routes>
    </Router>
  );
}

export default App;
