import React, { useState, useEffect } from 'react';
import UserPanel from './components/UserPanel/user_index';
import NavBar from './components/NavBar';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom'; // Import components from react-router-dom

const POST_HEADERS = {
  'Content-Type': 'application/json',
  'Accepts': 'application/json'
}

function App() {
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    async function checkSession() {
      const response = await fetch('/check_session')

      if (response.ok) {
        const data = await response.json()
        setCurrentUser(data)
      }
    }
    checkSession()
  }, [])

  async function attemptSignup(userInfo) {
    const res = await fetch('/users', {
      method: 'POST',
      headers: POST_HEADERS,
      body: JSON.stringify(userInfo)
    })
    if (res.ok) {
      const data = await res.json()
      setCurrentUser(data)
    } else {
      const errorData = await res.json();
      alert(errorData.error || 'Invalid sign up'); // Display the error from the backend
    }
  }

  async function attemptLogin(userInfo) {
    const res = await fetch('login', {
      method: 'POST',
      headers: POST_HEADERS,
      body: JSON.stringify(userInfo)
    })
    if (res.ok) {
      const data = await res.json()
      setCurrentUser(data)
    } else {
      alert('Invalid login') // Fix this message
    }
  }

  function logout() {
    setCurrentUser(null)
    fetch('/logout', {
      method: 'DELETE'
    })
  }

  return (
    <Router>
      <NavBar currentUser={currentUser} logout={logout} />
      <Routes>
        <Route path="/dashboard" element={<UserPanel currentUser={currentUser} attemptLogin={attemptLogin} attemptSignup={attemptSignup} logout={logout} />} />
        <Route path="/other-route" element={<OtherComponent />} />
        <Route path="/" element={<HomeComponent />} />
      </Routes>
    </Router>
  );
}

export default App;
