import React, { useEffect, useState } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

const containerStyle = {
  width: '800px',
  height: '400px',
  borderRadius: '5px'
};

const center = {
  lat: 32,
  lng: 20
};

const indexFunds = [
  { name: 'S&P 500', ticker: 'SPY' },
  { name: 'Euro Stoxx 50', ticker: 'STOXX50' },
  { name: 'Nikkei 225', ticker: 'NIKKEI225' },
  { name: 'S&P/ASX 200', ticker: 'ASX200' },
  { name: 'S&P/TSX Composite', ticker: 'TSX' }
];

const locations = [
  { name: 'United States', lat: 37.0902, lng: -95.7129 },
  { name: 'Europe', lat: 54.5260, lng: 15.2551 },
  { name: 'Asia', lat: 34.0479, lng: 100.6197 },
  { name: 'Australia', lat: -25.2744, lng: 133.7751 },
  { name: 'Canada', lat: 56.1304, lng: -106.3468 }
];

export default function MarketWatch() {
  const [apiKey, setApiKey] = useState("");
  const [error, setError] = useState(null);
  const [marketData, setMarketData] = useState([]);

  useEffect(() => {
    fetch('/api/maps/config')
      .then(response => response.json())
      .then(data => setApiKey(data.apiKey))
      .catch(error => {
        console.error("Error fetching Google Maps API key:", error);
        setError('Error loading Google Maps!');
      });
  }, []);

  useEffect(() => {
    // Fetch and update market data for each index fund here
    const fetchMarketData = async () => {
      const updatedMarketData = [];
      for (const fund of indexFunds) {
        try {
          const response = await fetch(`/api/fetch-market-data?ticker=${fund.ticker}`);
          const data = await response.json();
          updatedMarketData.push({ fund, data });
        } catch (error) {
          console.error(`Error fetching market data for ${fund.name}:`, error);
        }
      }
      setMarketData(updatedMarketData);
    };

    fetchMarketData();
  }, []);

  // Helper function to determine if the market is down
  const isMarketDown = (data) => {
    // Compare data.currentValue with data.yesterdayValue to decide
    // whether the market is down or not
    // You can define your criteria for this comparison.
    return data.currentValue < data.yesterdayValue;
  };

  return (
    <>
      {error ? (
        <p>{error}</p>
      ) : apiKey ? (
        <LoadScript googleMapsApiKey={apiKey}>
          <GoogleMap mapContainerStyle={containerStyle} center={center} zoom={2}>
            {indexFunds.map((fund, index) => (
              <Marker
                key={index}
                position={locations[index]} // Set the marker position based on country's location
                title={fund.name}
                icon={{
                  url: isMarketDown(marketData[index].data)
                    ? 'https://png.pngtree.com/png-vector/20221024/ourmid/pngtree-red-pin-marker-png-image_6385786.png'
                    : 'https://www.clipartmax.com/png/middle/182-1827658_pin-png-pin-icon-green.png',
                  scaledSize: { width: 32, height: 32 }
                }}
              />
            ))}
          </GoogleMap>
        </LoadScript>
      ) : (
        <p>Loading map...</p>
      )}
    </>
  );
}
